import { Photo, Photov3 } from "./api";
const settings = {
  headers: {
    Accept: "application/json",
    Authorization: "563492ad6f91700001000001ac22187f3df749b5ac359637c75cb9b5",
  },
};

// 取得照片
export const getPhoto = (num) =>
  Photo.get(`curated?page=${num}&per_page=15`, settings);

// 搜尋照片
export const getSearchPhoto = (currentSearch, num) =>
  Photo.get(`search?query=${currentSearch}&per_page=15&page=${num}`, settings);

// 搜尋照片
export const LikePhoto = (id) => Photov3.post(`media/${id}/like`, settings);
